class LabelCollectingApiRequest(ApiRequest):

	def __init__(self, request_bytes, start_dtime, end_dtime):
		super(LabelCollectingApiRequest, self).__init__(request_bytes)
		date_pat = re.compile(b'\d{8}')
		time_pat = re.compile(b'[0-2][0-9][0-5][0-9][0-5][0-9]')
		sday, stime = start_dtime
		eday, etime = end_dtime
		sday = sday.replace('-', '').encode()
		stime = stime.replace(':', '').encode()
		eday = eday.replace('-', '').encode()
		etime = etime.replace(':', '').encode()

		self.request = time_pat.sub(etime, self.request)
		self.request = time_pat.sub(stime, self.request, 2)
		self.request = date_pat.sub(eday, self.request)
		self.request = date_pat.sub(sday, self.request, 1)


class OrderSelectApiRequest(ApiRequest):
	# raws = []

	def __init__(self, ord_start_date, ord_end_date, wards):
		self.requests = []
		self.raws = []
		ord_start_date = ord_start_date.replace('-', '').encode()
		ord_end_date = ord_end_date.replace('-', '').encode()
		date_pat = re.compile(b'\d{8}')

		for word in wards:
			req = API_REQ['ordSelect'][word]
			req = date_pat.sub(ord_end_date, req)
			req = date_pat.sub(ord_start_date, req, 1)
			self.requests.append(req)

	def api_calls(self):

		for reqeust in self.requests:
			self.raws.append(super(OrderSelectApiRequest, self).api_call(request=reqeust))
			time.sleep(0.3)

		return self.raws

	def get_records(self):
		records = []
		for raw in self.raws:
			records += super(OrderSelectApiRequest, self).get_records('table1', raw_data=raw)
		return records

	def set_test_response(self, response_sample_path):
		self.raws.append(super(OrderSelectApiRequest, self).set_test_response(response_sample_path))
		return self

